const handleadd = (product, cartitems, setCartItems) => {
  debugger
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );

    } else {
      localStorage.setItem({"cart": [...cartitems, { ...product, quantity: 1 }]})      
      // setCartItems([...cartitems, { ...product, quantity: 1 }]
    }

  };

export default handleadd;
