import React,{useState} from "react";
import "./header.css";
import { useNavigate } from "react-router-dom";

function Header() {
  const navigate = useNavigate();
  const [cartitems, setCartItems] = useState([]);

  const handleadd = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );
    } else {
      setCartItems([...cartitems, { ...product, quantity: 1 }]);
    }
  };

  const handleremove = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit.quantity === 1) {
      setCartItems(cartitems.filter((item) => item?.id !== product?.id));
    } else {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity - 1 }
            : item
        )
      );
    }
  };

  const handleSubmit = () => {
    setCartItems([]);
  };

  return (
    <>
      <div className="header">
        <div>
          <h1>Shoping Center</h1>
        </div>

        <div>
          <img
            src="./pics/home.png"
            alt="home"
            className="home-img"
            onClick={() => navigate("/")}
          />
        </div>

        <div>
          <div className="total">{length}</div>
            <img src="./pics/cart.png" alt="cart" className="img" onClick={() => navigate("/cart")}
/>
        </div>
      </div>
    </>
  );
}

export default Header;
