import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";
import "./Cart.css";

function Cart() {
  const [name, setName] = useState("");
  const [address, setAddress] = useState([]);
  const [cartitems, setCartItems] = useState([]);
  const navigate = useNavigate();
  const { state } = useLocation();
  const [data, setData] = useState(state);

  const header = () => {
    navigate("/", { state: { cartitems: cartitems } });
  };

  const handleClick = () => {
    setAddress([...address, name]);
    setName("");
  };

  const handleadd = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );
    } else {
      setCartItems([...cartitems, { ...product, quantity: 1 }]);
    }
  };

  const handleremove = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit.quantity === 1) {
      setCartItems(cartitems.filter((item) => item?.id !== product?.id));
    } else {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity - 1 }
            : item
        )
      );
    }
  };

  // const totalPrice = cartitems.reduce(
  //   (price, item) => price + item.quantity * item.price,
  //   0
  // );

  // const length = cartitems?.reduce((length, item) => length + item.quantity, 0);

  const handleSubmit = () => {
    setData({});
  };

  console.log("state++++", data);

  return (
    <>
      <div className="cart-items">
        <div className="cart-items-quantity">
          <button className="home-btn" onClick={header}>
            Back To Home
          </button>
        </div>
        <div className="cart-items-header">
          <h2>Cart Items</h2>
        </div>
        {/* <div className="cart-items-quantity">
          <h3 className="length">{length}</h3>
        </div> */}
        {/* <div className="cart-items-header">
          {length === 0 && <p>For Shoping Please Click on Add Button</p>}
        </div> */}

        {data.map((item) => {
          return (
            <div className="cart">
              <div className="cart-item-list" key={item.id}>
                <img className="cart-items-image" src={item.image} alt="" />
                <div className="heading">
                  <h3>{item.id}</h3>
                  <h3>{item.name}</h3>
                  <h3>{item.price}</h3>
                </div>

                <div>
                  <button
                    className="cart-items-add"
                    onClick={() => handleadd(state)}
                  >
                    Add
                  </button>
                  <button
                    className="cart-items-remove"
                    onClick={() => handleremove(state)}
                    // disabled={length < 1}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          );
        })}
  {/* 
          <div className="cart-total">
            <h4>Total Bill = ${totalPrice}</h4>
          </div> */}

        <div className="cart-input">
          <input
            className="input"
            type="text"
            onChange={(e) => setName(e.target.value)}
            value={name}
            placeholder="Enter Address"
          />
          <button className="input-btn" onClick={handleClick}>
            Submit
          </button>
        </div>

        <div className="cart-total-button">
          <div className="container mt-3">
            <button
              type="button"
              className="btn btn-success"
              data-bs-toggle="modal"
              data-bs-target="#myModal"
              onClick={handleSubmit}
            >
              Order Now
            </button>
          </div>
        </div>
        <div className="modal" id="myModal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <img src="./pics/tick.png" alt="tick" className="tick-img" />
              </div>

              <div className="modal-body">
                {address.map((item, ind) => {
                  return <p key={ind}>Address : {item}</p>;
                })}
                <h6>Thanks for your submission.</h6>
                <p>We Will Contact You Soon.</p>
              </div>

              <div className="modal-footer">
                <button
                  type="button"
                  className="footer-btn"
                  data-bs-dismiss="modal"
                  onClick={() => setName("")}
                >
                  OK
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Cart;
