import { useState } from "react";
import Header from "./component/front/Header";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Product from "./component/Product/Product";
import Cart from "./component/front/Cart/Cart";
import data from "./component/backend Data/data";

function App() {
  const [cartitems, setCartItems] = useState([]);

  const handleadd = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );

    } else {
      setCartItems([...cartitems, { ...product, quantity: 1 }]);
      localStorage.setItem()
    }

  };

  const handleremove = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit.quantity === 1) {
      setCartItems(cartitems.filter((item) => item?.id !== product?.id));
    } else {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity - 1 }
            : item
        )
      );
    }
  };

  const handleSubmit = () => {
    setCartItems([]);
  };
  const { productItems } = data;

  return (
    <>
      <BrowserRouter>
        <Header cartitems={cartitems} />
        <Routes>
          <Route
            path="/"
            element={
              <Product 
              productItems={productItems}
               handleadd={handleadd} />
            }
          ></Route>
          <Route
            path="/cart"
            element={
              <Cart
                cartitems={cartitems}
                handleadd={handleadd}
                handleremove={handleremove}
                handleSubmit={handleSubmit}
              />
            }
          ></Route>
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
