import React from "react";

function Cart({ items, handleadd, handleremove }) {
    const totalPrice = items.reduce((price,item) => price + items.quantity * item.price,0)
  return (
    <>
      <div className="cart-items">
        <div className="cart-items-header">Cart Items</div>
        {items.length === 0 && <div>No Items are Add</div>}

        <div>
          {items.map((cartItem) => (
            <div key={cartItem.id} className="cart-item-list">
              <img
                className="cart-items-image"
                src={cartItem.image}
                alt={cartItem.name}
              />
              <div>{cartItem.name}</div>
              <div>{cartItem.price}</div>
              <div>
                <button onClick={() => handleadd(items)}>+</button>
                <button onClick={() => handleremove(items)}>-</button>
              </div>
              <div>
                {cartItem.quantity} * ${cartItem.price}
              </div>
              <div>
                Total Price
                <div>
                    {totalPrice}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Cart;
