import React, { useState, useEffect } from "react";
import store from "../Mobx/Mobx";
import styled from "styled-components";

function TodoList() {
  const [todo, setTodo] = useState([]);

  useEffect(() => {
    async function load() {
      let data = await store.loadTodos();
      setTodo(data);
    }
    load();
  }, []);

  return (
    <>
    <Button type="">Add</Button>
      <StyledDiv>
        <h1>Get Data from Api</h1>
        {todo.map((item, index) => {
          return <li key={index}>{item.todo}</li>;
        })}
      </StyledDiv>
    </>
  );
}

export default TodoList;

const StyledDiv = styled.div`
  margin: 20px;
  padding: 10px;
  border: 2px solid orange;
  width: 600px;
  border-radius: 20px;

  h1 {
    color: brown;
    border: 3px solid blue;
    width: 290px;
    border-radius: 10px;
  }

  li {
    font-family: "Courier New", Courier, monospace;
    font-size: larger;
  }
`;
const Button = styled.button`
  color: palevioletred;
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
`;
