import data from "../backend Data/data";
import "./Product.css";
import Header from "../front/Header";
import { useLocation } from "react-router-dom";

function Product() {
  const { productItems } = data;
  const { state } = useLocation();

  console.log("state", state);

  const handleadd = (product) => {
    const ProductExit = state?.cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      state?.setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );
    } else {
      state?.setCartItems([...state.cartitems, { ...product, quantity: 1 }]);
    }
  };
  return (
    <>
      <Header />
      <div className="cart-total-button"></div>
      <div className="products">
        {productItems.map((productItems) => {
          return (
            <div className="card" key={productItems.id}>
              <img
                className="product-image"
                src={productItems.image}
                alt={productItems.name}
              />
              <h5>{productItems.name}</h5>
              <p>${productItems.price}</p>
              <button
                className="button"
                onClick={() => handleadd(productItems.id)}
              >
                Add to Cart
              </button>
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Product;
