import { useState } from "react";
import data from "./component/backend Data/data";
import data1 from "./component/backend Data/data1";
import Header from "./component/front/Header";
import { BrowserRouter as Router } from "react-router-dom";
import Routes from "./component/Routes/Routes";

function App() {
  const [cartitems, setCartItems] = useState([]);
  const [address, setAddress] = useState("");
  const [cart, setCart] = useState([]);

  const handleAddress = () => {
    setCart([...cart, address]);
    setAddress("");
  };

  const { productItems } = data;
  const { contact } = data1;

  const handleadd = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );
    } else {
      setCartItems([...cartitems, { ...product, quantity: 1 }]);
    }
  };

  const handleremove = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit.quantity === 1) {
      setCartItems(cartitems.filter((item) => item?.id !== product?.id));
    } else {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity - 1 }
            : item
        )
      );
    }
  };

  const handleSubmit = () => {
    setCartItems([]);
  };

  return (
    <>
      <div>
        <input type="text" onChange={(e) => setAddress(e.target.value)} value={address} />
        <button onClick={handleAddress}>Add</button>
      </div>
      <div>
        {cart.map((item,index) => {
          return <p key={index}>{item}</p>;
        })}
      </div>
      {/* <Router>
        <Header cartitems={cartitems} />
        <Routes
          productItems={productItems}
          cartitems={cartitems}
          handleadd={handleadd}
          handleremove={handleremove}
          handleSubmit={handleSubmit}
          contact={contact}
        />
      </Router> */}
    </>
  );
}

export default App;
