import { useLocation } from "react-router-dom";
import "./Cart.css";
import Header from "../Header";

function Cart() {
  const { state } = useLocation();

  const handleadd = (product) => {
    const ProductExit = state.cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      state.setCartItems(
        state.cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );
    } else {
      state.setCartItems([...state.cartitems, { ...product, quantity: 1 }]);
    }
  };

  const handleremove = (product) => {
    const ProductExit = state.cartitems.find((item) => item?.id === product?.id);
    if (ProductExit.quantity === 1) {
      state.setCartItems(state.cartitems.filter((item) => item?.id !== product?.id));
    } else {
      state.setCartItems(
        state.cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity - 1 }
            : item
        )
      );
    }
  };

  const handleSubmit = () => {
    state.setCartItems([]);
  };

  // const enabled = cartitems.length > 0;

  // const totalPrice = cartitems.reduce(
  //   (price, item) => price + item.quantity * item.price,
