import React from "react";

function Cart({ items, handleadd, handleremove }) {
  const totalPrice = items.reduce(
    (price, item) => price + items.quantity * items.price,
    0
  );
  return (
    <>
      <div className="cart-items">
        <div className="cart-items-header">Cart Items</div>
        {items.length === 0 && <div>No Items are Add</div>}

        <div>
          {items.map((item) => (
            <div key={item.id} className="cart-item-list">
              <img
                className="cart-items-image"
                src={item.image}
                alt={item.name}
              />
              <div>
                <button onClick={() => handleadd(items)}>+</button>
                <button onClick={() => handleremove(items)}>-</button>
              </div>
              <div>
                {item.quantity} * ${item.price}
              </div>
              <div>
                Total Price
                <div>{totalPrice}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default Cart;
