import React, { useState, useEffect } from "react";
import store from "../Mobx/Mobx";
// import styled from "styled-components";

function TodoList() {
  const [todo, setTodo] = useState([]);

  useEffect(() => {
    async function load() {
      let data = await store.loadTodos();
      setTodo(data);
    }
    load();
  }, []);

  return (
    <>
      <bu>Add</bu>
      <div>
        <h1>Get Data from Api</h1>
        {todo.map((item, index) => {
          return <li key={index}>{item.todo}</li>;
        })}
      </div>
    </>
  );
}

export default TodoList;

