import React, { useState } from "react";
import "./Product.css";

function Product({ productItems, handleadd }) {
  const [address, setAddress] = useState("");
  const [cart, setCart] = useState([]);

  const handleAddress = () => {
    setCart([...cart, address]);
    setAddress("");
  };
  return (
    <>
      <div className="cart-total-button">
        <div className="container mt-3">
          <button
            type="button"
            className="btn btn-success"
            data-bs-toggle="modal"
            data-bs-target="#myModal"
          >
            Enter Address
          </button>
        </div>
      </div>

      <div className="modal" id="myModal">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">Enter Detail</div>

            <div className="modal-body">
              <div>
                <input
                  type="text"
                  onChange={(e) => setAddress(e.target.value)}
                  value={address}
                />
                <button onClick={handleAddress}>Add</button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="products">
        {productItems.map((productItems) => {
          return (
            <div className="card" key={productItems.id}>
              <img
                className="product-image"
                src={productItems.image}
                alt={productItems.name}
              />
              <h5>{productItems.name}</h5>
              <p>${productItems.price}</p>
              <button
                className="button"
                onClick={() => handleadd(productItems)}
              >
                Add to Cart
              </button>
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Product;
