import { useState } from "react";
import {useLocation} from 'react-router-dom';
import "./Cart.css";
import Header from '../Header';

function Cart({props}) {
  const [name, setName] = useState("");
  const [address, setAddress] = useState([]);

  const { state } = useLocation();

  const handleClick = () => {
    setAddress([...address, name]);
    setName("");
  };

  // const enabled = cartitems.length > 0;

  // const totalPrice = cartitems.reduce(
  //   (price, item) => price + item.quantity * item.price,
  //   0
  // );

  return (
    <>
        <Header />

      <div className="cart-items">
        <div className="cart-items-header">
          <h2>Cart Items</h2>
        </div>
        {/* <div className="cart-items-header">
          {cartitems.length === 0 && (
            <p>More Shoping Please Click on Home Button</p>
          )}
        </div> */}
{/* 
        <div className="cart">
          {cartitems.map((item) => (
            <div key={item.id} className="cart-item-list">
              <img
                className="cart-items-image"
                src={item.image}
                alt={item.name}
              />

              <div className="heading">
                <p>Item Name : {item.name}</p>
                <p>
                  Item Price : {item.quantity} X ${item.price}
                </p>
                <p>Quantity : {item.quantity}</p>
              </div>

              <div>
                <button
                  className="cart-items-add"
                  onClick={() => handleadd(item)}
                >
                  +
                </button>
                <button
                  className="cart-items-remove"
                  onClick={() => handleremove(item)}
                >
                  -
                </button>
              </div>
            </div>
          ))}
        </div> */}

        {/* <div className="cart-total">
          <h4>Total Bill = ${totalPrice}</h4>
        </div> */}

        <div className="cart-input">
          <input
            className="input"
            type="text"
            onChange={(e) => setName(e.target.value)}
            value={name}
            placeholder="Enter Address"
          />
          <button
            className="input-btn"
            onClick={handleClick}
          >
            Submit
          </button>
        </div>

        <div className="cart-total-button">
          <div className="container mt-3">
            <button
              type="button"
              className="btn btn-success"
              data-bs-toggle="modal"
              data-bs-target="#myModal"
              // onClick={handleSubmit}
              // disabled={!enabled}
            >
              Order Now
            </button>
          </div>
        </div>
        <div className="modal" id="myModal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <img src="./pics/tick.png" alt="tick" className="tick-img" />
              </div>

              <div className="modal-body">
                {address.map((item, ind) => {
                  return <p key={ind}>Address : {item}</p>;
                })}
                <h6>Thanks for your submission.</h6>
                <p>We Will Contact You Soon.</p>
              </div>

              <div className="modal-footer">
                <button
                  type="button"
                  className="footer-btn"
                  data-bs-dismiss="modal"
                  onClick={() => setName("")}
                >
                  OK
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Cart;
