import { useState } from "react";

function UseMemoHook() {
  const [add, setAdd] = useState("");
  const [minus, setMinus] = useState("");

  return (
    <>
      <div>
        <h1>Hello from Memo Hook</h1>
        <p>{add}</p>
        <button value={add} onChange={(e) => setAdd(e.target.value)}>Add</button>
        <input value={minus} onChange={(e) => setMinus(e.target.value)}></input>
      </div>
    </>
  );
}

export default UseMemoHook;
