import { toJS } from "mobx";
import { flow, types } from "mobx-state-tree";
import axios from "axios";

const todo = types.model({
  id: types.number,
  title: types.string,
  description: types.string,
  price: types.number,
  discountPercentage
});

const userTodo = types
  .model({
    todoList: types.array(todo),
  })

  .actions((self) => ({
    loadTodos: flow(function* loadTodos() {
      let res = yield axios.get("https://dummyjson.com/products");
      self.todoList = res.data.todos;
      return res.data.todos;
    }),
  }))

  .views((self) => ({
    getTodo() {
      return toJS(self.todoList);
    },
  }));

const store = userTodo.create({});

export default store;
