import React, { useState } from "react";
import data from "../backend Data/data";
import { useNavigate } from "react-router-dom";
import "./Product.css";
import Header from "../front/Header";

function Product() {
  // const [cartitems, setCartItems] = useState([]);
  const navigate = useNavigate();

  const { productItems } = data;

  const toComponentB = () => {
    navigate("/Header", { state: { cartitems: cartitems } });
  };

  return (
    <>
      <Header cartitems={cartitems} />
      <div className="cart-total-button"></div>
      <div className="products">
        {productItems.map((productItems) => {
          return (
            <div className="card" key={productItems.id}>
              <img
                className="product-image"
                src={productItems.image}
                alt={productItems.name}
              />
              <h5>{productItems.name}</h5>
              <p>${productItems.price}</p>
              <button
                className="button"
                onClick={() => handleadd(productItems)}
              >
                Add to Cart
              </button>
            </div>
          );
        })}
      </div>
    </>
  );
}

export default Product;
