import { useState, createContext } from "react";
import "./Cart.css";
import { useNavigate } from "react-router-dom";
import { useLocation } from "react-router-dom";

export const UserContext = createContext();

function Cart() {
  const [cartitems, setCartItems] = useState([]);

  const navigate = useNavigate();
  const { state } = useLocation();

  const header = () => {
    navigate("/", { state: { cartitems: cartitems } });
  };

  const handleadd = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit) {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity + 1 }
            : item
        )
      );
    } else {
      setCartItems([...cartitems, { ...product, quantity: 1 }]);
    }
  };

  const handleremove = (product) => {
    const ProductExit = cartitems.find((item) => item?.id === product?.id);
    if (ProductExit.quantity === 1) {
      setCartItems(cartitems.filter((item) => item?.id !== product?.id));
    } else {
      setCartItems(
        cartitems.map((item) =>
          item?.id === product?.id
            ? { ...ProductExit, quantity: ProductExit?.quantity - 1 }
            : item
        )
      );
    }
  };

  const totalPrice = cartitems.reduce(
    (price, item) => price + item.quantity * item.price,
    0
  );

  console.log("id............", state);

  return (
    <>
      <div className="cart-items">
        <div className="cart-items-header">
          <h2>Cart Items</h2>
        </div>
        <div className="cart-items-header">
          {state.length === 0 && (
            <p>More Shoping Please Click on Home Button</p>
          )}
        </div>

        <div className="cart">
          <div className="cart-item-list">
            {/* <img className="cart-items-image" src={state.image} /> */}
            {/* <div className="heading">
              <p>Item ID : {state.id}</p>
              <p>Item Name :{state.name}</p>
              <p>Item Price :${state.price}</p>
            </div> */}

            <div>
              <button
                className="cart-items-add"
                onClick={() => handleadd(state)}
              >
                +
              </button>
              <button
                className="cart-items-remove"
                onClick={() => handleremove(state)}
              >
                -
              </button>
            </div>
          </div>
        </div>

        <div className="cart-total">
          <h4>Total Bill = ${totalPrice}</h4>
        </div>

        <div className="cart-input">
          <button className="input-btn" onClick={header}>
            navigate
          </button>
        </div>
        <div className="modal" id="myModal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <img src="./pics/tick.png" alt="tick" className="tick-img" />
              </div>

              <div className="modal-body">
                <h6>Thanks for your submission.</h6>
                <p>We Will Contact You Soon.</p>
              </div>

              <div className="modal-footer">
                <button
                  type="button"
                  className="footer-btn"
                  data-bs-dismiss="modal"
                >
                  OK
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Cart;
